(function(){document.addEventListener('submit',function(e){var f=e.target;var u=f.getAttribute('hx-post');if(!u)return;e.preventDefault();var t=document.querySelector(f.getAttribute('hx-target'));var s=f.getAttribute('hx-swap')||'innerHTML';fetch(u,{method:'POST',body:new FormData(f)}).then(r=>r.text()).then(function(txt){if(t){if(s==='outerHTML'){t.outerHTML=txt;}else{t.innerHTML=txt;}}});});})();
